import os  
from openai import AzureOpenAI  
from azure.identity import DefaultAzureCredential, get_bearer_token_provider  
        
endpoint = os.getenv("ENDPOINT_URL", "https://aoaicalstrshub694201078865.openai.azure.com/")
deployment = os.getenv("DEPLOYMENT_NAME", "gpt-4o")
        
# Initialize Azure OpenAI client with Entra ID authentication  
cognitiveServicesResource = os.getenv('AZURE_COGNITIVE_SERVICES_RESOURCE', 'https://aisearchcalstrshub694201078865.search.windows.net')  
token_provider = get_bearer_token_provider(  
    DefaultAzureCredential(),  
    f'{cognitiveServicesResource}.default'  
)  
       
client = AzureOpenAI(  
    azure_endpoint=endpoint,  
    azure_ad_token_provider=token_provider,  
    api_version='2024-05-01-preview',  
)  
        
completion = client.chat.completions.create(  
    model=deployment,  
    messages=[
{
    "role": "system",
    "content": "You are an AI assistant that helps people find information."
},
{
    "role": "user",
    "content": "Where can I stay in New York?"
},
{
    "role": "assistant",
    "content": "Margie's Travel offers the following accommodation options in New York:\n\n1. **The Manhattan Hotel**: Stay in the heart of New York City, within an easy walk to Times Square and Broadway [doc1].\n2. **The Grand Central Hotel**: Comfortable mid-town hotel, close to Grand Central Station, the Chrysler Building, and the Empire State Building [doc1].\n3. **The Park Hotel**: Luxurious accommodation in upper Manhattan, with views of Central Park [doc1].\n\nTo book your trip to New York, visit [Margie's Travel](http://www.margiestravel.com) [doc1]."
}
],  
    past_messages=10,  
    max_tokens=800,  
    temperature=0.7,  
    top_p=0.95,  
    frequency_penalty=0,  
    presence_penalty=0,  
    stop=None,  
    extra_body={  
        "data_sources": [  
            {  
                "type": "azure_search",  
                "parameters": {  
                    "endpoint": os.environ["AZURE_AI_SEARCH_ENDPOINT"],  
                    "index_name": os.environ["AZURE_AI_SEARCH_INDEX"],  
                    "authentication": {  
                        "type": "azure_ad"  
                    }  
                }  
            }  
        ]  
    }  
)  
        
print(completion.model_dump_json(indent=2))
