    import os  
    from openai import AzureOpenAI  
    
    endpoint = os.getenv("ENDPOINT_URL", "https://azureoaieastus.openai.azure.com/")  
    deployment = os.getenv("DEPLOYMENT_NAME", "gpt-4o")  
    search_endpoint = os.getenv("SEARCH_ENDPOINT", "https://infoasst-search-ervkh.search.windows.net")  
    search_key = os.getenv("SEARCH_KEY", "put your Azure AI Search admin key here")  
    search_index = os.getenv("SEARCH_INDEX_NAME", "rayinplayground1")  
    subscription_key = os.getenv("AZURE_OPENAI_API_KEY", "REPLACE_WITH_YOUR_KEY_VALUE_HERE")  
    
    # Initialize Azure OpenAI client with key-based authentication
    client = AzureOpenAI(  
        azure_endpoint=endpoint,  
        api_key=subscription_key,  
        api_version="2024-05-01-preview",  
    )  
      
    # Prepare the chat prompt  
    chat_prompt = [
    {
        "role": "system",
        "content": "You are an AI assistant that helps people find information."
    },
    {
        "role": "user",
        "content": "What is the data in row 5?"
    },
    {
        "role": "assistant",
        "content": "The data in row 5 includes the following details:\n\n- **Response ID**: 318268\n- **Date**: 3/12/2024\n- **Channel**: In-Person\n- **Line of Business**: Driver Licenses and ID Cards\n- **Appointment**: Yes\n- **Experience Rating**: 7\n- **Service Comparison**: 5 - Way above expectations\n- **Staff Friendliness**: 1 - Yes\n- **Service Improvement**: 3 - Getting better\n- **Website Completion**: (Not provided)\n- **Website Ratings**: (Not provided)\n- **Comments**: \"I was getting an enhanced DL\"\n- **Primary Service**: Other\n- **Location**: Redmond\n- **Driver Document**: Permit\n- **Additional Comments**: (None provided)\n- **Sentiment Score**: 0\n\nThis row provides feedback about an in-person visit to the Redmond office for an enhanced driver license, which was rated very positively by the respondent [doc1]."
    },
    {
        "role": "user",
        "content": "What is the sentiment for response id 318961?"
    },
    {
        "role": "assistant",
        "content": "The sentiment for response ID 318961 is **0.7** [doc1]."
    },
    {
        "role": "user",
        "content": "For all future questions on sentiment, response with values between 1 to 5 with 1 being the lowest and 5 the highest. Respond only with a number."
    },
    {
        "role": "assistant",
        "content": "4"
    },
    {
        "role": "user",
        "content": "What is the sentiment for response id 318961?"
    },
    {
        "role": "assistant",
        "content": "4"
    },
    {
        "role": "user",
        "content": "What is the sentiment for response id 318968?"
    },
    {
        "role": "assistant",
        "content": "1"
    }
]  
    
    # Include speech result if speech is enabled  
    speech_result = chat_prompt  
    
    # Generate the completion  
    completion = client.chat.completions.create(  
        model=deployment,  
        messages=speech_result,  
        past_messages=10,  
        max_tokens=800,  
        temperature=0.7,  
        top_p=0.95,  
        frequency_penalty=0,  
        presence_penalty=0,  
        stop=None,  
        stream=False  
    )  
      
 ,
      extra_body={
        "data_sources": [{
          "type": "azure_search",
          "parameters": {
            "endpoint": f"{search_endpoint}",
            "index_name": "rayinplayground1",
            "semantic_configuration": "default",
            "query_type": "vector_semantic_hybrid",
            "fields_mapping": {},
            "in_scope": True,
            "role_information": "You are an AI assistant that helps people find information.",
            "filter": None,
            "strictness": 1,
            "top_n_documents": 20,
            "authentication": {
              "type": "api_key",
              "key": f"{search_key}"
            },
            "embedding_dependency": {
              "type": "deployment_name",
              "deployment_name": "text-embedding-ada-002"
            }
          }
        }]
      }   print(completion.to_json())
